import './App.css';
import {
  MDBInputGroup,
  MDBBtn
} from 'mdb-react-ui-kit';

import {useState} from 'react';

function App() {

  const [taskList, updateList] = useState([]);
  function addTask(){
    const taskInput = document.getElementById('taskInput');
    console.log(taskInput.value);
    updateList([...taskList,taskInput.value]);
  }

  return (
    <div className="App">
      <main className='container p-5'>
        <h1>To Do List App</h1>
        <form className='mt-5 mb-3' onSubmit={(e)=> e.preventDefault()}>

          <MDBInputGroup className='mb-3' >
            <MDBBtn color='light' rippleColor='dark' disabled>Enter the task:</MDBBtn>
            <input className='form-control' type='text' placeholder='Get groceries' id='taskInput'/>
            <MDBBtn color='success' onClick={addTask}>Add Task</MDBBtn>
          </MDBInputGroup>

        </form>
        <div className='p-3 d-flex flex-column gap-2'>

            {taskList.map((taskItem)=> <MDBBtn color='light' rippleColor='dark'>{taskItem}</MDBBtn>)}

        </div>
      </main>
    </div>
  );
}

export default App;
